function jia(a, b) {
    return a + b;
}

function jian(a, b) {
    return a - b;
}

function cheng(a, b) {
    return a * b;
}

function chu(a, b) {
    if (b == 0) {
        return "除数不能为0";
    } else {
        return a / b;
    }
}

function baifeng(a) {
    return a / 100;
}

function fu(a) {
    return 0 - a;
}

module.exports = {
    jia: jia,
    jian: jian,
    cheng: cheng,
    chu: chu,
    baifeng: baifeng,
    fu: fu
}